import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import SearchInput from "./SearchInput";
import SearchResults from "./SearchResults";

const SearchModal = ({ searchBooks, searchResults, addBookToShelf, books, getBooks, setSearchResults}) => {
  const [query, setQuery] = useState("");

  useEffect(() => {
    getBooks();
    return () => {
      setSearchResults([]);
    };
  }, []);

  const handleSearch = (e) => {
    e.preventDefault();
    const value = e.target.value;
    setQuery(value);
    searchBooks(value);
  };
  
  const updatedSearchResults = searchResults.map((result) => {
    const bookInShelf = books.find((book) => book.id === result.id);
    if (bookInShelf) {
      return {
        ...result,
        shelf: bookInShelf.shelf,
      };
    } else {
      return result;
    }
  });

  return (
    <div className="search-books">
      <div className="search-books-bar">
        <Link to="/" className="close-search" onClick={() => {setQuery('');}}>
          Close
        </Link>
        <SearchInput onChange={handleSearch} value={query} />
      </div>
      <SearchResults searchResults={updatedSearchResults} addBookToShelf={addBookToShelf}/>
    </div>
  );
};

export default SearchModal;
