import "./App.css";
import { useEffect, useState } from "react";
import { Route, Routes } from "react-router-dom";
import BookshelfContainer from "./components/BookshelfContainer";
import SearchModal from "./components/SearchModal";
import * as BooksAPI from "./BooksAPI";

function App() {
  const [books, setBooks] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  const getBooks = async () => {
    const res = await BooksAPI.getAll();
    setBooks(res);
  };


  const updateBookShelf = (book, shelf) => {
    const update = async () => {
      const res = await BooksAPI.update(book, shelf);
      setBooks((prevBooks) => prevBooks.map((prevBook) => 
          prevBook.id === book.id ? { ...prevBook, shelf } : prevBook
        )
      );
    };
    update();
  };

  const searchBooks = (query) => {
    const search = async () => {
      const res = await BooksAPI.search(query);
      setSearchResults(res);
    };
    search();
  }

  const addBookToShelf = async (book, shelf) => {
    await BooksAPI.update(book, shelf);
    const updatedBooks = books.map((prevBook) =>
      prevBook.id === book.id ? { ...prevBook, shelf } : prevBook
    );
    setBooks(updatedBooks);
  };

  useEffect(() => {
    getBooks();
  }, []);

  console.log(books)

  return (
    <Routes>
      <Route
        exact
        path="/"
        element={
          <BookshelfContainer books={books} updateBookShelf={updateBookShelf}/>
        }
      />
      <Route 
        path="/search"
        element={
          <SearchModal searchBooks={searchBooks} searchResults={searchResults} addBookToShelf={addBookToShelf}/>
        }
      />
    </Routes>
  );
}

export default App;
